#include <opencv2/opencv.hpp>
#include <iostream>

using namespace cv;
using namespace std;

 MatND getHistogram(Mat &image)
{
    MatND hist;
    int channels[] = {0};
    int dims = 1;
    int histSize[] = {256};
    float granges[] = {0, 255};
    const float *ranges[] = {granges};
    calcHist(&image, 1, channels, Mat(), hist, dims, histSize, ranges);
    return hist;
}
 //  
 Mat getHistogramImage(Mat &image)
{
    MatND hist = getHistogram(image);
    Mat showImage(256,256, CV_8U,Scalar(0));
    int i;
    double maxValue = 0;
    minMaxLoc(hist, 0, &maxValue, 0, 0);
    for(i = 0; i < 256; i++)
    {
        float value = hist.at<float>(i);
        int intensity = saturate_cast<int>(256 - 256* (value/maxValue));
        rectangle(showImage, Point(i,256 - 1), Point((i+1)-1, intensity), Scalar(255));
    }
    return showImage;
}

int main()
{
    Mat src= imread("original.jpeg");// 
    Mat gray,equa;
    Mat grayHistogram,equaHistogram;

    if(!src.data)             // 
      {
          cout <<  "" << endl ;
          return -1;
      }

    namedWindow("RGB",WINDOW_AUTOSIZE);   // 
    imshow("RGB",src);    // 

    cvtColor(src, gray, COLOR_BGR2GRAY);
    namedWindow("GRAY",WINDOW_AUTOSIZE);
    imshow("GRAY",gray);    // 

    grayHistogram = getHistogramImage(gray);
    //
    namedWindow("FirstHistogram",WINDOW_AUTOSIZE);
    imshow("FirstHistogram",grayHistogram); // 

    equalizeHist(gray, equa);
    namedWindow("EQUA",WINDOW_AUTOSIZE); 
    imshow("EQUA",equa);    // 

    imwrite("result.jpg", equa); // 

    equaHistogram = getHistogramImage(equa);  //
    namedWindow("SecondHistogram",WINDOW_AUTOSIZE; 
 imshow("SecondHistogram",equaHistogram);    // 

    waitKey(0); // 0

    src.release();
    gray.release();
    equa.release();
    grayHistogram.release();
    equaHistogram.release();
    destroyWindow("RGB");
    destroyWindow("GRAY");
    destroyWindow("EQUA");
    destroyWindow("FirstHistogram");
    destroyWindow("SecondHistogram");
    return 0;
}
```

